package com.restmonkeys.aoc

import java.io.File

fun main() {
    val input: List<Int> = File("/workspaces/aoc-2021/1_1.txt").readLines().map { it.toInt()}
    var result = 0
    for(i in 1..(input.size-1)) {
        if(input[i] > input[i-1]) result++
    }
    print(result)
}
